                data-testid="button-toggle-video"
              >
                {mediaState.video ? <Video className="h-4 w-4" /> : <VideoOff className="h-4 w-4" />}
                {!isMobile && <span className="ml-2">{mediaState.video ? 'Video On' : 'Video Off'}</span>}
