                {mediaState.audio ? <Mic className="h-4 w-4" /> : <MicOff className="h-4 w-4" />}
                {!isMobile && <span className="ml-2">{mediaState.audio ? 'Mic On' : 'Mic Off'}</span>}
              </Button>

